import Products from './components/products'
import Testing from './components/testing';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import Home from './components/Home';
import SingleProduct from './components/singleproduct';
import React from "react";
import { gsap } from "gsap";

const { useLayoutEffect, useRef } = React;
const Box = ({ children, className, anim }) => {
  return <div className={"box " + className } data-animate={ anim }>{children}</div>;
};console.clear();


function App() {
  const app = useRef();

  useLayoutEffect(() => {

    const ctx = gsap.context(() => {
      // Target the two specific elements we have asigned the animate class
      gsap.to("[data-animate='rotate']", {
        rotation: 360,
        repeat: -1,
        repeatDelay: 1,
        yoyo: true
      });

      gsap.to("[data-animate='move']", {
        x: 100,
        repeat: -1,
        repeatDelay: 1,
        yoyo: true
      });

      gsap.set(".dont-animate", {
        backgroundColor: 'red'
      });


    }, app);// <- Scope!

    return () => ctx.revert();
  }, []);

  return (
      <>
    <div className="app" ref={app}>
      <Box anim="rotate">Box</Box>
      <Box className="dont-animate">Don't Animate Me</Box>
      <Box anim="move">Box</Box>
    </div>
    <div class='space' id='spacer1'>Hello world</div>

<div class='wrapper'>
  <div class='headers'>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
    <h1>test</h1>
  </div>
</div>

<div class='space' id='spacer2'>bye world</div>

  <BrowserRouter>
    <Routes>
      <Route path="/" element={ <Home/> }></Route>
      <Route path="products" element={<Products/>} ></Route>
      <Route path="testing" element={<Testing/>} ></Route>
      <Route path="products/:productId" element={<SingleProduct/>}></Route>
    </Routes>
  </BrowserRouter>
  </>
  );
}

export default App;
