import Products from './components/products'
import Testing from './components/testing';
import {BrowserRouter, Routes, Route, Link} from 'react-router-dom';
import Home from './components/Home';
import SingleProduct from './components/singleproduct';
//const { useEffect, useLayoutEffect, useRef, useState } = React;
import gsap from 'gsap';
import {useRef, useEffect} from 'react';
import {ScrollTrigger} from 'gsap/ScrollTrigger'
gsap.registerPlugin(ScrollTrigger)

function App() {
  const imgRef = useRef(null);
  useEffect(()=> {
    const el = imgRef.current;
    //gsap.fromTo(el, {opacity: 0}, {opacity:1, duration: 3 })

    let mobileTL = gsap.timeline({
      scrollTrigger: {
        trigger: ".marq-cont",
        start: "-100% bottom",
        scrub: 1,
      }
    });

    mobileTL.to(".first", {duration: 2, xPercent: -100, color: offWhite})
            .to(".second", {duration: 2, xPercent: 100, color: offWhite},"<")
            .to(".container", {duration: 0.5, backgroundColor: black},"<")
            .to(".cta", {duration: 2, color: offWhite},"<");


}, [])
  return (
  <>
  <div>
    <div className='orange' > 
    div 1
    <div className='red' ref={imgRef}>div 2</div>
    </div>
  </div>
  <BrowserRouter>
    <Routes>
      <Route path="/" element={ <Home/> }></Route>
      <Route path="products" element={<Products/>} ></Route>
      <Route path="testing" element={<Testing/>} ></Route>
      <Route path="products/:productId" element={<SingleProduct/>}></Route>
    </Routes>
  </BrowserRouter>
  </>

  );
}

export default App;
